mapper.insert:
INSERT INTO tb_body_info (
	body_info_date, age, height, weight, gender, target_weight, exercise_mass
	, bmi, target_calorie, target_carbs, target_protein, target_fat, target_sugar
	, member_no
) VALUES (
	NOW(), #{age}, #{height}, #{weight}, #{gender}, #{target_weight}, #{exercise_mass}
	, #{bmi}, #{target_calorie}, #{target_carbs}, #{target_protein}, #{target_fat}, #{target_sugar}
	, #{member_no}
)


	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE tb_body_info
SET age = #{age}
	, height = #{height}
	, weight = #{weight}
	, gender = #{gender}
	, target_weight = #{target_weight}
	, exercise_mass = #{exercise_mass}
	, bmi = #{bmi}
	, target_calorie = #{target_calorie}
	, target_carbs = #{target_carbs}
	, target_protein = #{target_protein}
	, target_fat = #{target_fat}
	, target_sugar = #{target_sugar}
WHERE body_info_no = #{body_info_no};

mapper.insert:
INSERT INTO tb_body_change (
	regist_date, weight, muscle_mass, fat_mass, member_no
) VALUES (
	NOW(), #{weight}, #{muscle_mass}, #{fat_mass}, #{member_no}
)


	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE tb_body_change
SET weight = #{weight}
	, muscle_mass = #{muscle_mass}
	, fat_mass = #{fat_mass}
WHERE body_change_no = #{body_change_no} AND regist_date = CURDATE();
