mapper.update:
UPDATE tb_member
SET member_loginFailCnt = #{member.member_loginFailCnt}
WHERE member_id = #{member.member_id}

UPDATE tb_member
SET member_loginBlocked = 1
WHERE member_id = #{member.member_id}

mapper.delete:
DELETE FROM tb_member
WHERE member_id=#{member_id} AND
member_pw = SHA2(#{member_pw}, 256);
